import React, { useState, useRef, useEffect } from "react";

export default function App() {
  const [input, setInput] = useState("");
  const [chatLog, setChatLog] = useState([]);
  const fileInputRef = useRef(null);
  const chatEndRef = useRef(null);

  const fileToBase64 = (file) =>
    new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });

  const sendMessage = async (imageDataUrl = null) => {
    if (!input.trim() && !imageDataUrl) return;

    if (input.trim())
      setChatLog((prev) => [...prev, { sender: "user", text: input }]);
    if (imageDataUrl)
      setChatLog((prev) => [...prev, { sender: "user", image: imageDataUrl }]);

    try {
      const body = imageDataUrl
        ? { image_url: imageDataUrl }
        : { message: input };

      const response = await fetch("/api/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(body),
      });

      const data = await response.json();

      if (data.reply) {
        setChatLog((prev) => [...prev, { sender: "bot", text: data.reply }]);
      }
      if (data.reply_image_url) {
        setChatLog((prev) => [
          ...prev,
          { sender: "bot", image: data.reply_image_url },
        ]);
      }
    } catch (error) {
      setChatLog((prev) => [
        ...prev,
        { sender: "bot", text: "서버와 통신 중 오류가 발생했습니다." },
      ]);
    }

    setInput("");
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") sendMessage();
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const base64 = await fileToBase64(file);
    sendMessage(base64);
    e.target.value = null;
  };

  useEffect(() => {
    if (chatEndRef.current) {
      chatEndRef.current.scrollIntoView({ behavior: "smooth" });
    }
  }, [chatLog]);

  return (
    <div className="app-container">
      <header className="app-header">
        <div className="app-logo">DIMP</div>
      </header>

      <main className="chatbox">
        <div className="chatlog">
          {chatLog.map((msg, i) => (
            <div
              key={i}
              className={`chat-message ${
                msg.sender === "user" ? "user" : "bot"
              }`}
            >
              {msg.text && <span className="message-text">{msg.text}</span>}
              {msg.image && (
                <img
                  src={msg.image}
                  alt="chat-img"
                  className="message-image"
                />
              )}
            </div>
          ))}
          <div ref={chatEndRef} />
        </div>

        <div className="input-area">
          <input
            type="text"
            placeholder="메시지를 입력하세요"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={handleKeyPress}
            className="input-text"
          />
          <label htmlFor="image-upload" className="image-upload-label" title="사진 첨부">
            📷
          </label>
          <input
            id="image-upload"
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            style={{ display: "none" }}
          />
          <button onClick={() => sendMessage()} className="send-button" title="전송">
            ▶
          </button>
        </div>
      </main>
    </div>
  );
}
